"""backprop_network.py"""import randomimport numpy as npimport math# import matplotlib.pyplot as pltb0_norm = []b1_norm = []b2_norm = []b3_norm = []b4_norm = []class Network(object):    def __init__(self, sizes):        """The list ``sizes`` contains the number of neurons in the        respective layers of the network.  For example, if the list        was [2, 3, 1] then it would be a three-layer network, with the        first layer containing 2 neurons, the second layer 3 neurons,        and the third layer 1 neuron.  The biases and weights for the        network are initialized randomly, using a Gaussian        distribution with mean 0, and variance 1.  Note that the first        layer is assumed to be an input layer, and by convention we        won't set any biases for those neurons, since biases are only        ever used in computing the outputs from later layers."""        self.num_layers = len(sizes)        self.sizes = sizes        self.biases = [np.random.randn(y, 1) for y in sizes[1:]]        self.weights = [np.random.randn(y, x)                        for x, y in zip(sizes[:-1], sizes[1:])]    def SGD(self, training_data, epochs, mini_batch_size, learning_rate,            test_data):        """Train the neural network using mini-batch stochastic        gradient descent.  The ``training_data`` is a list of tuples        ``(x, y)`` representing the training inputs and the desired        outputs.  """        training_accuracy = []        test_accuracy = []        training_loss = []        epoch_range = [i for i in range(epochs)]        print("Initial test accuracy: {0}".format(self.one_label_accuracy(test_data)))        n = len(training_data)        for j in range(epochs):            random.shuffle(list(training_data))            mini_batches = [                training_data[k:k + mini_batch_size]                for k in range(0, n, mini_batch_size)]            for mini_batch in mini_batches:                self.update_mini_batch(mini_batch, learning_rate)            print("Epoch {0} test accuracy: {1}".format(j, self.one_label_accuracy(test_data)))            # best_test_accuracy = self.one_label_accuracy(test_data)            # test_accuracy.append(self.one_label_accuracy(test_data))            # training_accuracy.append(self.one_hot_accuracy(training_data))            # training_loss.append(self.loss(training_data))        # print("Test accuracy for final epoch: ", best_test_accuracy)        # plt.plot(epoch_range, test_accuracy, color='red')        # plt.plot(epoch_range, training_accuracy, color='blue')        # plt.plot(epoch_range, training_loss, color = 'green')        # plt.xlabel("epochs number")        # plt.ylabel("accuracy for test and training data, loss for training loss")        # plt.title("Test/Training data accuracy by epochs and Training loss")        # plt.legend(['Test accuracy', 'Training accuracy', 'Training loss'], loc='lower left')        # plt.show()        # plt.close()        # plt.plot(epoch_range, training_loss)        # plt.title("Training loss by epochs")        # plt.xlabel("epochs number")        # plt.ylabel("loss")        # plt.show()        # plt.plot(epoch_range, b0_norm, color='red')        # plt.plot(epoch_range, b1_norm, color='blue')        # plt.plot(epoch_range, b2_norm, color='green')        # plt.plot(epoch_range, b3_norm, color='yellow')        # plt.plot(epoch_range, b4_norm, color='brown')        # plt.xlabel("epochs number")        # plt.ylabel("norm size")        # plt.legend(['b0', 'b1', 'b2', 'b3','b4'], loc='upper right')        # plt.show()    def update_mini_batch(self, mini_batch, learning_rate):        """Update the network's weights and biases by applying        stochastic gradient descent using backpropagation to a single mini batch.        The ``mini_batch`` is a list of tuples ``(x, y)``."""        nabla_b = [np.zeros(b.shape) for b in self.biases]        nabla_w = [np.zeros(w.shape) for w in self.weights]        for x, y in mini_batch:            delta_nabla_b, delta_nabla_w = self.backprop(x, y)            nabla_b = [nb + dnb for nb, dnb in zip(nabla_b, delta_nabla_b)]            nabla_w = [nw + dnw for nw, dnw in zip(nabla_w, delta_nabla_w)]        self.weights = [w - (learning_rate / len(mini_batch)) * nw                        for w, nw in zip(self.weights, nabla_w)]        self.biases = [b - (learning_rate / len(mini_batch)) * nb                       for b, nb in zip(self.biases, nabla_b)]        # b0_norm.append(np.linalg.norm(nabla_b[0]) / len(mini_batch))        # b1_norm.append(np.linalg.norm(nabla_b[1]) /len(mini_batch))        # b2_norm.append(np.linalg.norm(nabla_b[2]) /len(mini_batch))        # b3_norm.append(np.linalg.norm(nabla_b[3]) /len(mini_batch))        # b4_norm.append(np.linalg.norm(nabla_b[4]) /len(mini_batch))    def get_vector_list(self, x):        v_list = []        for i in range(len(self.weights)):            linear_func = np.add(np.dot(self.weights[i], x), self.biases[i])            if i != len(self.weights) - 1:                x = sigmoid(linear_func)            v_list.append(linear_func)        return v_list    def backprop(self, x, y):        """The function receives as input a 784 dimensional        vector x and a one-hot vector y.        The function should return a tuple of two lists (db, dw)        as described in the assignment pdf. """        # forward pass        v_list = self.get_vector_list(x)        z_list = [x] + [sigmoid(vector) for vector in v_list]        bias_derivatives = [None for i in range(len(self.biases))]        weight_derivatives = [None for i in range(len(self.weights))]        # back prop        loss_deriv = self.loss_derivative_wr_output_activations(v_list[-1], y)        sigmoid_derivative_list = [sigmoid_derivative(v) for v in v_list]        bias_derivatives[-1] = loss_deriv        for i in range(len(self.weights) - 2, -1, -1):            bias_derivatives[i] = np.dot(self.weights[i + 1].transpose(), bias_derivatives[i + 1]) * \                                  sigmoid_derivative_list[i]        for i in range(len(self.weights)):            weight_derivatives[i] = np.dot(bias_derivatives[i], z_list[i].transpose())        return bias_derivatives, weight_derivatives    def one_label_accuracy(self, data):        """Return accuracy of network on data with numeric labels"""        output_results = [(np.argmax(self.network_output_before_softmax(x)), y)                          for (x, y) in data]        return sum(int(x == y) for (x, y) in output_results) / float(len(data))    def one_hot_accuracy(self, data):        """Return accuracy of network on data with one-hot labels"""        output_results = [(np.argmax(self.network_output_before_softmax(x)), np.argmax(y))                          for (x, y) in data]        return sum(int(x == y) for (x, y) in output_results) / float(len(data))    def network_output_before_softmax(self, x):        """Return the output of the network before softmax if ``x`` is input."""        layer = 0        for b, w in zip(self.biases, self.weights):            if layer == len(self.weights) - 1:                x = np.dot(w, x) + b            else:                x = sigmoid(np.dot(w, x) + b)            layer += 1        return x    def loss(self, data):        """Return the loss of the network on the data"""        loss_list = []        for (x, y) in data:            net_output_before_softmax = self.network_output_before_softmax(x)            net_output_after_softmax = self.output_softmax(net_output_before_softmax)            loss_list.append(np.dot(-np.log(net_output_after_softmax).transpose(), y).flatten()[0])        return sum(loss_list) / float(len(data))    def output_softmax(self, output_activations):        """Return output after softmax given output before softmax"""        output_exp = np.exp(output_activations)        return output_exp / output_exp.sum()    def loss_derivative_wr_output_activations(self, output_activations, y):        """Return derivative of loss with respect to the output activations before softmax"""        return self.output_softmax(output_activations) - ydef sigmoid(z):    """The sigmoid function."""    return 1.0 / (1.0 + np.exp(-z))def sigmoid_derivative(z):    """Derivative of the sigmoid function."""    return sigmoid(z) * (1 - sigmoid(z))